I needed encoding of an UTF-8 string as base64 for a project of mine. Most of the answers here don&#39;t seem to properly handle UTF-16 surrogate pairs when converting to UTF-8 so, for completion sake, I will post my solution:

    function strToUTF8Base64(str) {
    
    	function decodeSurrogatePair(hi, lo) {
    		var resultChar = 0x010000;
    		resultChar += lo - 0xDC00;
    		resultChar += (hi - 0xD800) &lt;&lt; 10;
    		return resultChar;
    	}
    
    	var bytes = [0, 0, 0];
    	var byteIndex = 0;
    	var result = [];
    
    	function output(s) {
    		result.push(s);
    	}
    
    	function emitBase64() {
    
    		var digits =
    				&#39;ABCDEFGHIJKLMNOPQRSTUVWXYZ&#39; +
    				&#39;abcdefghijklmnopqrstuvwxyz&#39; +
    				&#39;0123456789+/&#39;;
    
    		function toDigit(value) {
    			return digits[value];
    		}
    
    		// --Byte 0--    --Byte 1--    --Byte 2--
    		// 1111  1122    2222  3333    3344  4444
    
    		var d1 = toDigit(bytes[0] &gt;&gt; 2);
    		var d2 = toDigit(
    			((bytes[0] &amp; 0x03) &lt;&lt; 4) |
    			(bytes[1] &gt;&gt; 4));
    		var d3 = toDigit(
    			((bytes[1] &amp; 0x0F) &lt;&lt; 2) |
    			(bytes[2] &gt;&gt; 6));
    		var d4 = toDigit(
    			bytes[2] &amp; 0x3F);
    
    		if (byteIndex === 1) {
    			output(d1 + d2 + &#39;==&#39;);
    		}
    		else if (byteIndex === 2) {
    			output(d1 + d2 + d3 + &#39;=&#39;);
    		}
    		else {
    			output(d1 + d2 + d3 + d4);
    		}
    	}
    
    	function emit(chr) {
    		bytes[byteIndex++] = chr;
    		if (byteIndex == 3) {
    			emitBase64();
    			bytes[0] = 0;
    			bytes[1] = 0;
    			bytes[2] = 0;
    			byteIndex = 0;
    		}
    	}
    
    	function emitLast() {
    		if (byteIndex &gt; 0) {
    			emitBase64();
    		}
    	}
    
    	// Converts the string to UTF8:
    
    	var i, chr;
    	var hi, lo;
    	for (i = 0; i &lt; str.length; i++) {
    		chr = str.charCodeAt(i);
    
    		// Test and decode surrogate pairs in the string
    		if (chr &gt;= 0xD800 &amp;&amp; chr &lt;= 0xDBFF) {
    			hi = chr;
    			lo = str.charCodeAt(i + 1);
    			if (lo &gt;= 0xDC00 &amp;&amp; lo &lt;= 0xDFFF) {
    				chr = decodeSurrogatePair(hi, lo);
    				i++;
    			}
    		}
    
    		// Encode the character as UTF-8.
    		if (chr &lt; 0x80) {
    			emit(chr);
    		}
    		else if (chr &lt; 0x0800) {
    			emit((chr &gt;&gt; 6) | 0xC0);
    			emit(((chr &gt;&gt; 0) &amp; 0x3F) | 0x80);
    		}
    		else if (chr &lt; 0x10000) {
    			emit((chr &gt;&gt; 12) | 0xE0);
    			emit(((chr &gt;&gt;  6) &amp; 0x3F) | 0x80);
    			emit(((chr &gt;&gt;  0) &amp; 0x3F) | 0x80);
    		}
    		else if (chr &lt; 0x110000) {
    			emit((chr &gt;&gt; 18) | 0xF0);
    			emit(((chr &gt;&gt; 12) &amp; 0x3F) | 0x80);
    			emit(((chr &gt;&gt;  6) &amp; 0x3F) | 0x80);
    			emit(((chr &gt;&gt;  0) &amp; 0x3F) | 0x80);
    		}
    	}
    
    	emitLast();
    
    	return result.join(&#39;&#39;);
    }

