    	if (resize_canvas === true) {
    		canvas.width = width;
    		canvas.height = height;
    	} else {
    		ctx.clearRect(0, 0, width_source, height_source);
    	}
    
    	//draw
    	ctx.putImageData(img2, 0, 0);
