    		navigator.appVersion.indexOf(&quot;MSIE 9&quot;)==-1 &amp;&amp;	//v9 is ok
    		navigator.appVersion.indexOf(&quot;MSIE 1&quot;)==-1	//v10, 11, 12, etc. is fine too
    	);
    						
    	if(badBrowser){
    		// navigate to error page
    	}
    }

